var fakeGPIO = function()
{
	var fakePin = function(number)
	{
		this.number = number;
		this.value = 0;
		
		this.set = function(val)
		{
			this.value = val;
			console.log("Set pin "+this.number+" to "+val+".");
		}
		
		this.on = function(event, callback)
		{
			var r = !! Math.round(Math.random() * 1);
			if (r)
			{
				callback(1);
			} else {
				callback(0);
			}
		}
	}

	this.export = function(number, data)
	{
		if (data == undefined)
		{
			console.log("Exported pin "+number+".");
		} else {
			console.log("Exported pin "+number+" with data "+JSON.stringify(data)+".");
		}
		return new fakePin(number);
	}
}

module.exports = new fakeGPIO;